/* eslint no-undef: [0, { typeof: true }] */
import { expect } from 'chai';
import { Router } from '../router';
import { Store } from '../store';
import PixelRoot from './pixel';

describe('Pixel root', () => {
  it('should have store and router', () => {
    expect(PixelRoot).haveOwnProperty('router').instanceOf(Router);
    expect(PixelRoot).haveOwnProperty('store').instanceOf(Store);
  });
});

import Router from './router';

export { Router };
export { IRoutesConfig, IRouterState, IDefaultRoute } from './router.type';

import { Pixel } from '..';
import { IDefaultRoute, IRoutes, IRoutesConfig } from './router.type';

class Router {
  static ERRORS: Record<string, string> = {
    NO_DEF_CONF: 'Router default config not found',
    NO_DEF_ROUTES: 'Router default path not found',
    WRONG_DEF_COMP: 'Router default component has not found',
    NO_ROUTES: 'Path has not been added to the router',
    WRONG_COMP: 'Router has not found required component',
  };

  static EVENTS: Record<string, string> = {
    INIT: 'init',
    UPDATE: 'update',
  };

  static instantce: Router;

  private pixelInstantce: typeof Pixel;

  public defaultRoute: IDefaultRoute;

  public routes: IRoutes;

  public history: History;

  private currentPlace: string;

  private withAuth: boolean = true;

  private isAuth: boolean = false;

  private permittedRoutes: string[];

  private redirect: string;

  private authRedirect: string;

  constructor(pixelInstantce?: typeof Pixel) {
    if (Router.instantce) {
      return Router.instantce;
    }

    this.setPixelInstantce(pixelInstantce);

    this.history = window.history;

    Router.instantce = this;
  }

  public setPixelInstantce(pixelInstantce?: typeof Pixel) {
    if (pixelInstantce) {
      this.pixelInstantce = pixelInstantce;
    }
  }

  public handleAuthChange(isAuth: boolean) {
    if (!isAuth) {
      this.isAuth = false;
    } else {
      this.isAuth = true;
    }

    this.checkRoute(window, true);
  }

  public async setRoutes(routesConfig: IRoutesConfig) {
    const { routes, defaultRoute, auth } = routesConfig;

    try {
      if (!defaultRoute) {
        throw Error(Router.ERRORS.NO_DEF_CONF);
      }

      if (!defaultRoute.path) {
        throw Error(Router.ERRORS.NO_DEF_ROUTES);
      }

      if (!this.pixelInstantce.components[defaultRoute.component]) {
        throw Error(Router.ERRORS.WRONG_DEF_COMP);
      }

      if (!routes) {
        throw Error(Router.ERRORS.NO_ROUTES);
      }

      Object.keys(routes).forEach((key) => {
        if (!routes[key] || !this.pixelInstantce.components[routes[key].component]) {
          throw Error(Router.ERRORS.WRONG_COMP);
        }
      });

      this.defaultRoute = defaultRoute;
      this.routes = { ...routes, [defaultRoute.path]: defaultRoute };

      if (auth) {
        const { redirect, check, permittedRoutes, authRedirect } = auth;
        this.redirect = redirect;
        this.authRedirect = authRedirect;
        this.permittedRoutes = permittedRoutes;
        await check();
      } else {
        this.withAuth = false;
      }

      this.start();
    } catch (error) {
      console.error(error);
    }
  }

  private start = () => {
    window.onpopstate = (event: any) => {
      const target = event.currentTarget as Window;
      this.checkRoute(target);
    };

    this.checkRoute(window);
  };

  public go(pathname: string) {
    this.history.pushState({ page: pathname }, '', pathname);
    this.checkRoute(window);
  }

  private checkRoute = (target: Window = window, authChange?: boolean) => {
    let currentRoute = target.location.pathname.slice(1);

    if (!currentRoute) {
      currentRoute = this.defaultRoute.path;
    } else if (!this.routes[currentRoute]) {
      currentRoute = 'wrong';
    }

    if (this.currentPlace !== currentRoute || authChange) {
      if (this.withAuth) {
        this.replaceWithAuth(currentRoute);
      } else {
        this.changeLayout(this.routes[currentRoute].component, currentRoute);
      }
    }
  };

  private replaceWithAuth(currentRoute: string) {
    const isFromBasePermitted = this.permittedRoutes.some((route) => route === currentRoute);

    if (!this.isAuth && !isFromBasePermitted) {
      this.go(this.redirect);
    } else if (this.isAuth && isFromBasePermitted) {
      this.go(this.authRedirect);
    } else {
      this.changeLayout(this.routes[currentRoute].component, currentRoute);
    }
  }

  public changeLayout = (componentName: string, currentRoute: string) => {
    this.currentPlace = currentRoute;
    this.pixelInstantce.render(`<${componentName}  />`);
  };
}

export default Router;

interface IRouterState {
  history: History;
}

interface IBaseAuth {
  check: () => Promise<any>;
  redirect: string;
  authRedirect: string;
  permittedRoutes: string[];
}

interface IDefaultRoute {
  path: string;
  component: string;
  title: string;
}

interface IRouteConfig {
  component: string;
  title: string;
}
interface IRoutes {
  wrong: IRouteConfig;
  error: IRouteConfig;
  [key: string]: IRouteConfig;
}

interface IRoutesConfig {
  auth?: IBaseAuth;
  routes: IRoutes;
  defaultRoute: IDefaultRoute;
}

export { IRouterState, IRoutesConfig, IDefaultRoute, IRoutes };

import { EVENTS } from '../../const';
import { ParentNodeType } from '../pixelDom';
import { Pixel } from '../root';
import { ICurrentUserStore } from './store.type';

export class Store {
  static ERRORS = {
    missStoreField: (field: string) => `Store does't have field - ${field}`,
  };

  pixelInstantce: typeof Pixel;

  protected authListenters: Record<string, any>[] = [];

  public currentUser: ICurrentUserStore;

  store: Record<string, any>;

  listeners: Record<string, ParentNodeType[]> = {};

  constructor(pixelInstantce: typeof Pixel) {
    this.pixelInstantce = pixelInstantce;
    this.authListenters.push(this.pixelInstantce.router);

    this.currentUser = this.createUserStore();
  }

  init(baseStore: Record<string, any> = {}) {
    this.store = this.createStore({ ...baseStore, currentUser: this.currentUser });
  }

  createStore(baseStore: Record<string, any>) {
    const self = this;
    const validator = {
      get: (target: Record<string, any>, key: string): unknown => target[key],
      set: (target: Record<string, any>, prop: string, value: unknown) => {
        if (prop in target) {
          target[prop] = value;
          self.emit(prop);
          return true;
        }

        console.error(Store.ERRORS.missStoreField(prop));

        return false;
      },
      deleteProperty: () => false,
    };

    return new Proxy(baseStore, validator);
  }

  createUserStore() {
    const self = this;
    const validator = {
      get: (target: ICurrentUserStore, key: string): unknown => target[key],
      set: (target: ICurrentUserStore, prop: string, value: unknown) => {
        target[prop] = value;

        if (prop === 'isAuth') {
          self.emitAuthListenters(value as boolean);
        }
        return true;
      },
      deleteProperty: () => false,
    };

    return new Proxy({ isAuth: false }, validator);
  }

  subscribe(field: string, listener: ParentNodeType) {
    if (!this.listeners[field]) {
      this.listeners[field] = [];
      this.store[field] = null;
    }

    this.listeners[field].push(listener);
  }

  unsubscribe = (event: string, listener: ParentNodeType) => {
    if (!this.listeners[event]) {
      throw new Error(`Event ${event} is undefined `);
    }

    this.listeners[event] = this.listeners[event].filter((item) => item !== listener);
  };

  dispatch(field: string, value: any) {
    this.store[field] = value;
  }

  setUserData(data: ICurrentUserStore) {
    Object.keys(data).forEach((key) => {
      this.currentUser[key] = data[key];
    });
  }

  protected emit = (field: string) => {
    if (this.listeners[field]) {
      this.listeners[field].forEach((listener) => {
        listener.eventBus.emit(EVENTS.PSU, [field, this.store[field]]);
      });
    }
  };

  protected emitAuthListenters = (isAuth: boolean) => {
    this.authListenters.forEach((listener) => {
      if (listener && listener.handleAuthChange) {
        listener.handleAuthChange(isAuth);
      }
    });
  };
}

export enum EVENTS {
  NDM = 'node-did-mount',
  NU = 'node-unmount',
  PSU = 'pixel-store-update',
}

interface ICurrentUserStore {
  isAuth?: boolean;
  [key: string]: any;
}

type IPixelStoreUpdateProp = [string, any];

export { ICurrentUserStore, IPixelStoreUpdateProp };

import { Parser } from '../parser';
import { ParentNodeType, IComponentModel, pixelDOM } from '../pixelDom';
import { IRoutesConfig, Router } from '../router';
import { EVENTS } from '../../const';
import { Store } from '../store';

export interface IPixelInstance {
  el: string;
  template?: string;
  routerConfig?: IRoutesConfig;
  store?: Record<string, any>;
  components?: Record<string, Function>;
}

class Pixel {
  static ERROR = {
    ROOT_NF: (selector: string) => `Root element ${selector} is not found`,
    ROOT_DOM_NF: (tagName: string) => `Root dom element ${tagName} has not created valid dom node`,
    VDOM_NF: 'Failed to build VDOM',
    PIXEL_ISALRE: 'Failed to build VDOM',
    INITIATED: 'Pixel already in use',
    FRONG_COMPONENT: (name: string) => `Component "${name}" is not registered`,
  };

  static CONST = {
    CDM: EVENTS.NDM,
  };

  protected static instance: Pixel;

  public router: Router;

  public store: Store;

  private root: Element;

  private VDOM: ParentNodeType;

  public components: Record<string, Function> = {};

  protected isInitiated: boolean = false;

  public initiatedComponents: Record<string, IComponentModel> = {};

  constructor() {
    if (Pixel.instance) {
      return Pixel.instance;
    }

    this.router = new Router(this);
    this.store = new Store(this);

    Pixel.instance = this;
  }

  config(config: IPixelInstance) {
    if (this.isInitiated) {
      throw new Error(Pixel.ERROR.INITIATED);
    }

    this.setRootEl(config.el);
    this.store.init(config.store);
    this.registerComponents(config.components);
    this.init(config.routerConfig, config.template);
    this.isInitiated = true;
  }

  registerComponents(components: { [key: string]: Function } | undefined): void {
    if (components) {
      Object.keys(components).forEach((componentName: string) => {
        if (!this.components[componentName]) {
          this.components[componentName] = components[componentName];
        }
      });
    }
  }

  callComponentModel(componentName: string) {
    if (this.components[componentName]) {
      const componentModel = this.components[componentName]();
      componentModel.name = componentName;
      return componentModel;
    }

    throw Error(Pixel.ERROR.FRONG_COMPONENT(componentName));
  }

  setRootEl = (selector: string) => {
    try {
      const root = document.querySelector(selector);
      if (!root) {
        throw Error(Pixel.ERROR.ROOT_NF(selector));
      } else {
        this.root = root;
      }
    } catch (err) {
      console.error(err);
    }
  };

  init = (routerConfig: IRoutesConfig | null = null, template: string | null = null) => {
    if (routerConfig) {
      this.router.setRoutes(routerConfig);
    } else if (template) {
      this.render(template);
    }
  };

  render = (template: string) => {
    try {
      if (this.VDOM) {
        pixelDOM.unmount(this.VDOM);
      }

      this.VDOM = Parser.parseHTML(template);

      if (!this.VDOM) {
        throw Error(Pixel.ERROR.VDOM_NF);
      }

      this.mount(this.VDOM);
    } catch (error) {
      console.error(error);
    }
  };

  mount = (VDOM: ParentNodeType) => {
    try {
      pixelDOM.render(VDOM);

      if (!VDOM.domEl) {
        throw Error(Pixel.ERROR.ROOT_DOM_NF(VDOM.tagName));
      }

      if (this.root.childNodes.length) {
        this.root.replaceChild(VDOM.domEl, this.root.childNodes[0]);
      } else {
        this.root.appendChild(VDOM.domEl);
      }
    } catch (error) {
      console.error(error);
    }
  };
}

const PixelRoot = new Pixel();

export default PixelRoot;

export const PixelRouter = PixelRoot.router;
export const PixelStore = PixelRoot.store;

import { ParentNodeType, pixelDOM, VCommonNode, VirtualNode, VTextNode } from '../pixelDom';
import { Stack } from '../utils';
import { TagParser } from './tagParser';
import { ComponentParser } from './componentParser';
import { IData, IParentData } from './parser.type';
import { TextParser } from './textParser';

export default class PixelParser {
  tagRegExp = /<[\s*]?[a-zA-Z0-9\-!/](?:"[^"]*"|'[^']*'|[^'">])*>/g;

  componentRegExp = /<[\\/]?[\s*]?[A-Z\-!](?:"[^"]*"|'[^']*'|[^'">])*>/g;

  replaceRegExp = new RegExp(/{{([^{}]*)}}/g);

  tagParser: TagParser;

  componentParser: ComponentParser;

  textParser: TextParser;

  constructor() {
    this.tagParser = new TagParser(this);
    this.componentParser = new ComponentParser(this);
    this.textParser = new TextParser(this);
  }

  parseHTML(html: string, parentProps?: IParentData): ParentNodeType {
    const stack = new Stack<ParentNodeType>();
    const ignoreStack = new Stack<ParentNodeType>();
    const tagReg = new RegExp(this.tagRegExp);
    let el = null;

    const { componentProps = {}, state = {}, methods = {} } = parentProps || {};
    const data: IData = { props: componentProps, state, methods };

    do {
      el = tagReg.exec(html);
      if (el) {
        const isIgnoreStackEmpty = ignoreStack.isEmpty();
        const tag = el[0];
        const { index } = el;

        const isComponent = this.isComponent(tag);
        const isXHTML = this.isXHTML(tag);
        const isCloseTag = tag[1] === '/';

        if (isComponent) {
          if (isIgnoreStackEmpty) {
            const component = this.componentParser.parse(tag, parentProps);

            if (component && Array.isArray(component)) {
              component.forEach((item) => this.addAsChild(stack, item));
            } else if (component) {
              this.addAsChild(stack, component);
            }
          }
        } else if (isXHTML) {
          if (isIgnoreStackEmpty) {
            const parsedTag = this.tagParser.parse(tag, data);

            if (parsedTag.isDisplay) {
              const node: VCommonNode = pixelDOM.nodeFabric.create(parsedTag) as VCommonNode;

              this.addAsChild(stack, node);
            }
          }
        } else if (isCloseTag) {
          if (isIgnoreStackEmpty) {
            const node = stack.pop();

            this.addAsChild(stack, node);
          } else {
            ignoreStack.pop();
          }
        } else {
          let textNode = null;
          const start = index + tag.trim().length;
          const nextChar = html[start];

          const parsedTag = this.tagParser.parse(tag, data);
          const node: VCommonNode = pixelDOM.nodeFabric.create(parsedTag) as VCommonNode;

          if (nextChar && nextChar !== '<') {
            const text = html.slice(start, html.indexOf('<', start)).trim();
            textNode = this.textParser.parse(text, data);
          }

          if (isIgnoreStackEmpty && parsedTag.isDisplay) {
            stack.push(node);

            if (textNode) {
              this.addAsChild(stack, textNode);
            }
          } else {
            ignoreStack.push(node);
          }
        }
      }
    } while (el);

    return stack.pop();
  }

  addAsChild = (stack: Stack<ParentNodeType>, node: VirtualNode) => {
    const parent = stack.peek();

    if (parent) {
      parent.children.push(node);
    } else if (!(node instanceof VTextNode)) {
      stack.push(node);
    }
  };

  isComponent = (tag: string) => tag.match(this.componentRegExp);

  isXHTML = (tag: string) => tag[tag.length - 2] === '/';
}

export const Parser = new PixelParser();

export { pixelDOM } from './pixelDom';
export { NODE_TYPE } from './const';
export * from './Nodes';

import { NODE_TYPE, PATCH_TYPE } from './const';
import NodeFabric from './NodeFabric';
import { ParentNodeType, VTextNode, VirtualNode, VComponentNode, Props } from './Nodes';
import { EVENTS } from '../../const';
import { IPatch, PropsDiff } from './pixelDom.type';
import { BFS } from '../utils';

class PixelDOM {
  static instantce: PixelDOM;

  nodeFabric: NodeFabric;

  constructor() {
    if (PixelDOM.instantce) {
      return PixelDOM.instantce;
    }

    this.nodeFabric = new NodeFabric();

    PixelDOM.instantce = this;
  }

  render(node: VirtualNode) {
    if (node instanceof VTextNode) {
      return this.mountTextNode(node);
    }

    return this.mountNode(node);
  }

  mountTextNode = (node: VTextNode): VTextNode => {
    const domNode = document.createTextNode(node.text);
    node.domEl = domNode;
    return node;
  };

  mountNode = (node: ParentNodeType): VirtualNode => {
    const domNode = window.document.createElement(node.tagName);
    node.domEl = domNode;
    node.eventBus.emit(EVENTS.NDM);

    this.setProps(node);

    /* link between parent and children */
    node.children.forEach((child: VirtualNode, index: number) => {
      child.setParentNode(node);

      if (child instanceof VTextNode) {
        this.mountTextNode(child);
      } else {
        child.keyIndex = index;
        this.mountNode(child);
      }

      domNode.appendChild(child.domEl);
    });

    return node;
  };

  setProps = (node: ParentNodeType, newNode?: ParentNodeType, pDiff?: PropsDiff) => {
    if (pDiff) {
      pDiff.forEach((value, key) => {
        if (value === undefined) {
          this.removeAttribute(node, key, value);
        } else {
          this.setAttribute(node, key, value);
        }
        node.updateProps(newNode!.props);
      });
    } else {
      const props = Object.entries(node.props);
      props.forEach(([key, value]) => {
        this.setAttribute(node, key, value);
      });
    }
  };

  setAttribute = (node: ParentNodeType, key: string, value: string | boolean) => {
    if (typeof value === 'boolean' || key === 'value') {
      (node.domEl as any).value = value;
    } else if (value) {
      node.domEl.setAttribute(key, value);
    }
  };

  removeAttribute = (node: ParentNodeType, key: string, value: string | boolean) => {
    if (typeof value === 'boolean') {
      (node.domEl as any).value = false;
    } else if (key === 'value') {
      (node.domEl as any).value = '';
    } else {
      node.domEl.removeAttribute(key);
    }
  };

  patch = (oldNode: VComponentNode, newNode: VComponentNode) => {
    const patches: IPatch[] = [];
    this.diff(oldNode, newNode, patches);
    this.update(patches);
  };

  diff = (oldNode: VirtualNode, newNode: VirtualNode, patches: IPatch[]) => {
    if (oldNode.type !== newNode.type) {
      patches.push({
        type: PATCH_TYPE.REPLACE,
        newNode,
        oldNode,
      });
    } else if (newNode instanceof VTextNode) {
      if ((oldNode as VTextNode).text !== newNode.text) {
        patches.push({
          type: PATCH_TYPE.TEXT,
          newNode,
          oldNode,
        });
      }
    } else if ((oldNode as ParentNodeType).tagName !== newNode.tagName) {
      patches.push({
        type: PATCH_TYPE.REPLACE,
        newNode,
        oldNode,
      });
    } else {
      const pDiff: PropsDiff = this.diffProps(oldNode.props, newNode.props);

      if (pDiff.size) {
        patches.push({
          type: PATCH_TYPE.PROPS,
          newNode,
          oldNode,
          pDiff,
        });
      }

      this.diffChildren(oldNode as ParentNodeType, newNode, patches);
    }
  };

  diffChildren = (oldNode: ParentNodeType, newNode: ParentNodeType, patches: IPatch[]) => {
    const oChildren = oldNode.children;
    const nChildren = newNode.children;
    const maxLength = Math.max(oChildren.length, nChildren.length);

    for (let i = 0; i < maxLength; i += 1) {
      const oChild = oChildren[i];
      const nChild = nChildren[i];

      if (!nChild) {
        patches.push({
          type: PATCH_TYPE.DELETE,
          oldNode: oChild,
          newNode: nChild,
        });
      } else if (!oChild) {
        patches.push({
          type: PATCH_TYPE.ADD,
          oldNode: oChild,
          newNode: nChild,
          parent: oldNode,
        });
      } else {
        this.diff(oChild, nChild, patches);
      }
    }
  };

  diffProps = (oldProps: Props, newProps: Props) => {
    const diff: PropsDiff = new Map();

    for (const key in oldProps) {
      if (oldProps[key] !== newProps[key]) {
        diff.set(key, newProps[key]);
      }
    }

    for (const key in newProps) {
      if (oldProps[key] === undefined) {
        diff.set(key, newProps[key]);
      }
    }

    return diff;
  };

  update(patches: IPatch[]) {
    patches.forEach((patch) => {
      const { oldNode, newNode, type, pDiff, parent } = patch;
      if (type === PATCH_TYPE.REPLACE) {
        this.replace(oldNode, newNode);
      } else if (type === PATCH_TYPE.TEXT) {
        (oldNode as VTextNode).updateText((newNode as VTextNode).text);
        oldNode.domEl.textContent = (newNode as VTextNode).text;
      } else if (type === PATCH_TYPE.PROPS) {
        this.setProps(oldNode as ParentNodeType, newNode as ParentNodeType, pDiff);
      } else if (type === PATCH_TYPE.ADD) {
        const node = this.render(newNode);
        node.setParentNode(parent!);

        parent?.domEl.appendChild(node.domEl);
        parent?.children.push(node);
        parent?.children.forEach((child, index) => {
          child.keyIndex = index;
        });
      } else if (type === PATCH_TYPE.DELETE) {
        this.removeChild(oldNode);
      }
    });
  }

  replace(oldNode: VirtualNode, newNode: VirtualNode) {
    const { parent } = oldNode;

    const node = this.render(newNode);
    node.setParentNode(parent);
    node.keyIndex = oldNode.keyIndex;

    parent.children[oldNode.keyIndex] = node;
    parent.domEl.replaceChild(node.domEl, oldNode.domEl);

    this.unmount(oldNode);
  }

  removeChild(node: VirtualNode) {
    node.parent.domEl.removeChild(node.domEl);
    node.parent.children.splice(node.keyIndex, 1);
    node.parent.children.forEach((child, index) => {
      child.keyIndex = index;
    });
    this.unmount(node);
  }

  unmount = (node: VirtualNode) => {
    const callUnmount = (vnode: VirtualNode) => {
      if (!(vnode instanceof VTextNode)) {
        vnode.eventBus.emit(EVENTS.NU);
      }
    };

    BFS(node, callUnmount);
  };
}

const pixelDOM = new PixelDOM();

export { pixelDOM, NODE_TYPE };

enum NODE_TYPE {
  TEXT_NODE = 'TEXT_NODE',
  COMMON_NODE = 'COMMON_NODE',
  COMPONENT_NODE = 'COMPONENT_NODE',
}

enum PATCH_TYPE {
  ADD = 'ADD',
  TEXT = 'TEXT',
  PROPS = 'PROPS',
  REPLACE = 'REPLACE',
  UPDATE = 'UPDATE',
  DELETE = 'DELETE',
}

export { NODE_TYPE, PATCH_TYPE };

import { IParsedTag } from '../parser';
import {
  VCommonNode,
  VTextNode,
  VComponentNode,
  ITextNodeProps,
  IComponentOptions,
  INodeProps,
  IInitiatedComponent,
} from './Nodes';

export default class NodeFabric {
  public create(config: IInitiatedComponent | INodeProps | ITextNodeProps, componentParsed?: IParsedTag) {
    if ('name' in config) {
      return this.createComponent(config, componentParsed!);
    }

    if ('tagName' in config) {
      return this.createNode(config);
    }

    return this.createText(config);
  }

  private createNode = (props: INodeProps): VCommonNode => new VCommonNode(props);

  private createText = (props: ITextNodeProps): VTextNode => new VTextNode(props);

  private createComponent = (componentConfig: IInitiatedComponent, parsed: IParsedTag): VComponentNode => {
    const props: IComponentOptions = {
      componentProps: parsed.props,
      pixelStore: componentConfig.pixelStore,
      state: componentConfig.state,
      name: componentConfig.name,
      methods: componentConfig.methods || {},
      componentDidMount: componentConfig.componentDidMount,
      componentWillUnmount: componentConfig.componentWillUnmount,
      componentDidUpdate: componentConfig.componentDidUpdate,
    };

    return new VComponentNode(props);
  };
}

